INSERT INTO `event_tft_table` VALUES (24, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"油槽车之前装过汽油\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"向油槽车内卸入灯油\"}],\"gradeId\":\"\",\"label\":\"装过汽油的油槽车在装入煤油的过程中槽内原有汽油不断挥发\"}],\"gradeId\":\"\",\"label\":\"油槽内形成高浓度易燃易爆汽油蒸气\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"外界空气进入油槽内\"}],\"gradeId\":\"IA7\",\"label\":\"A7$油槽内形成爆炸性混合气体\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"Ic4\",\"label\":\"c4$输油流速过大\"}],\"gradeId\":\"\",\"label\":\"静电产生\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"输油管不够长\"}],\"gradeId\":\"\",\"label\":\"输油管头上接了一段绝缘管\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"输油管未接地\"}],\"gradeId\":\"\",\"label\":\"静电积聚\"}],\"gradeId\":\"Ic\",\"label\":\"c$产生静电火花\"}],\"gradeId\":\"\",\"label\":\"油槽车帽口附近着火\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"工作人员在帽口附近负责输油操作\"}],\"gradeId\":\"\",\"label\":\"1名工作人员被烧伤\"}],\"gradeId\":\"\",\"label\":\"油槽车帽口起火事故P31\"}', '油槽车帽口起火事故P31,1名工作人员被烧伤,油槽车帽口附近着火,油槽内形成高浓度易燃易爆汽油蒸气,装过汽油的油槽车在装入煤油的过程中槽内原有汽油不断挥发,油槽车之前装过汽油,向油槽车内卸入灯油,外界空气进入油槽内,静电产生,静电积聚,输油管头上接了一段绝缘管,输油管不够长,输油管未接地,工作人员在帽口附近负责输油操作');
INSERT INTO `event_tft_table` VALUES (25, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"油罐内油品挥发\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"外界空气进入\"}],\"gradeId\":\"IA7\",\"label\":\"A7$油罐内形成爆炸性混合气体\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"Ic1\",\"label\":\"c1$油品从罐顶进入，油品剧烈喷溅\"}],\"gradeId\":\"\",\"label\":\"静电产生\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"罐内液位计上用的金属漂浮物断线后漂浮在油面\"}],\"gradeId\":\"\",\"label\":\"静电积聚\"}],\"gradeId\":\"Ic\",\"label\":\"c$产生静电火花\"}],\"gradeId\":\"\",\"label\":\"油罐爆炸着火\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"消防队开错阀门，误认为消防系统失灵\"}],\"gradeId\":\"\",\"label\":\"消防队用泡沫钩管将油罐中火焰扑灭\"}],\"gradeId\":\"\",\"label\":\"大火被扑灭\"}],\"gradeId\":\"\",\"label\":\"1977年某炼油厂油罐爆炸事故P32\"}', '1977年某炼油厂油罐爆炸事故P32,大火被扑灭,油罐爆炸着火,油罐内油品挥发,外界空气进入,静电产生,静电积聚,罐内液位计上用的金属漂浮物断线后漂浮在油面,消防队用泡沫钩管将油罐中火焰扑灭,消防队开错阀门，误认为消防系统失灵');
INSERT INTO `event_tft_table` VALUES (26, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"罐内碱渣上面浮着一层汽油，汽油挥发\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"空气\"}],\"gradeId\":\"IA7\",\"label\":\"A7$罐内气体达到爆炸极限\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"开泵之前管内充满汽油\"}],\"gradeId\":\"\",\"label\":\"装渣时需用碱渣将汽油顶出\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"碱渣进口设在罐上部\"}],\"gradeId\":\"Ic1\",\"label\":\"c1$汽油从罐上部喷洒下落，油品剧烈喷溅\"}],\"gradeId\":\"\",\"label\":\"静电产生\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"油、水、碱渣等混合物在冲击下，产生大量泡沫\"}],\"gradeId\":\"\",\"label\":\"泡沫及其他浮游物收集静电\"}],\"gradeId\":\"\",\"label\":\"静电积聚\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"罐壁\"}],\"gradeId\":\"\",\"label\":\"放电部位\"}],\"gradeId\":\"Ic\",\"label\":\"c$静电火花\"}],\"gradeId\":\"\",\"label\":\"碱渣罐爆炸\"}],\"gradeId\":\"\",\"label\":\"1977年某炼厂碱渣罐爆炸事故P37\"}', '1977年某炼厂碱渣罐爆炸事故P37,碱渣罐爆炸,罐内碱渣上面浮着一层汽油，汽油挥发,空气,静电产生,装渣时需用碱渣将汽油顶出,开泵之前管内充满汽油,碱渣进口设在罐上部,静电积聚,泡沫及其他浮游物收集静电,油、水、碱渣等混合物在冲击下，产生大量泡沫,放电部位,罐壁');
INSERT INTO `event_tft_table` VALUES (27, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"IA2\",\"label\":\"A2$进入罐内油品油温高，喷气燃料急剧挥发\"}],\"gradeId\":\"IA6\",\"label\":\"A6$罐内喷气燃料蒸气积聚\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"送风调合\"}],\"gradeId\":\"\",\"label\":\"空气进入罐内\"}],\"gradeId\":\"IA7\",\"label\":\"A7$罐内形成爆炸性混合气体\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"or\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"调合风卷起油旋、泡沫、油雾\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"油内翻滚类似沉降起电\"}],\"gradeId\":\"\",\"label\":\"静电产生\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"调合风卷起油旋、泡沫、油雾\"}],\"gradeId\":\"\",\"label\":\"泡沫收集电荷\"}],\"gradeId\":\"\",\"label\":\"静电积聚\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"罐壁\"}],\"gradeId\":\"\",\"label\":\"放电部位\"}],\"gradeId\":\"Ic\",\"label\":\"c$静电火花\"}],\"gradeId\":\"\",\"label\":\"储罐爆炸，局部变形\"}],\"gradeId\":\"\",\"label\":\"1978年某厂储罐爆炸事故P38\"}', '1978年某厂储罐爆炸事故P38,储罐爆炸，局部变形,空气进入罐内,送风调合,静电产生,调合风卷起油旋、泡沫、油雾,油内翻滚类似沉降起电,静电积聚,泡沫收集电荷,调合风卷起油旋、泡沫、油雾,放电部位,罐壁');
INSERT INTO `event_tft_table` VALUES (28, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"罐内残留原油油气挥发\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"罐抽空停用\"}],\"gradeId\":\"\",\"label\":\"空气进入罐内\"}],\"gradeId\":\"IA7\",\"label\":\"A7$原油罐内形成爆炸性混合气体\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"雷击\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"施工质量差\"}],\"gradeId\":\"\",\"label\":\"油罐钢筋连接点外露\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"避雷针安装不符合要求\"}],\"gradeId\":\"\",\"label\":\"避雷设施失效\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"接地不良\"}],\"gradeId\":\"Ib2\",\"label\":\"b2$油罐钢筋连接点因雷击产生火花\"}],\"gradeId\":\"\",\"label\":\"原油罐被雷电击中，爆炸起火\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"一面扑救着火油罐\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"一面出水冷却相邻油罐\"}],\"gradeId\":\"\",\"label\":\"现场消防救援\"}],\"gradeId\":\"\",\"label\":\"大火很快扑灭，保护了临近油罐的安全\"}],\"gradeId\":\"\",\"label\":\"1979年炼油厂原油罐雷击爆炸事故P85\"}', '1979年炼油厂原油罐雷击爆炸事故P85,大火很快扑灭，保护了临近油罐的安全,原油罐被雷电击中，爆炸起火,罐内残留原油油气挥发,空气进入罐内,罐抽空停用,雷击,油罐钢筋连接点外露,施工质量差,避雷设施失效,避雷针安装不符合要求,接地不良,现场消防救援,一面扑救着火油罐,一面出水冷却相邻油罐');
