INSERT INTO `event_tft_table` VALUES (35, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"丙烯酸甲酯罐内爆炸性混合气体\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"IIIC3\",\"label\":\"C3$丙烯酸甲酯处于燃烧浓度范围内\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"IIIC1\",\"label\":\"C1$活性炭处理丙烯酸甲酯罐排出气体\"}],\"gradeId\":\"IIIC2\",\"label\":\"C2$活性炭吸附热蓄积，温度上升而自燃\"}],\"gradeId\":\"\",\"label\":\"丙烯酸甲酯蒸汽着火\"}],\"gradeId\":\"\",\"label\":\"火焰逆行进入罐内\"}],\"gradeId\":\"\",\"label\":\"丙烯酸甲酯混合气体爆炸\"}],\"gradeId\":\"\",\"label\":\"1976年兵库县化工厂丙烯酸甲酯罐爆炸事故\"}', '1976年兵库县化工厂丙烯酸甲酯罐爆炸事故,丙烯酸甲酯混合气体爆炸,丙烯酸甲酯罐内爆炸性混合气体,火焰逆行进入罐内,丙烯酸甲酯蒸汽着火');
