INSERT INTO `event_tft_table` VALUES (37, '{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"丙烯酸冬季在罐内凝固\"}],\"gradeId\":\"\",\"label\":\"为了从罐中取出丙烯酸，对罐加热并用泵汲出，然后罐放置冷却\"}],\"gradeId\":\"\",\"label\":\"丙烯酸在反复熔融和凝固中，聚合抑制剂像液体一侧转移并被汲出\"}],\"gradeId\":\"\",\"label\":\"罐内剩下不含聚合抑制剂的丙烯酸\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"IVD4\",\"label\":\"D4$丙烯酸易发生聚合反应\"}],\"gradeId\":\"IVD1\",\"label\":\"D1$聚合反应放热\"}],\"gradeId\":\"\",\"label\":\"反应因温度上升而失控，丙烯酸蒸汽压上升\"}],\"gradeId\":\"\",\"label\":\"蒸汽爆炸\"}],\"gradeId\":\"\",\"label\":\"丙烯酸雾滴分散在空气中\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"点火源\"}],\"gradeId\":\"\",\"label\":\"1969年神奈川县丙烯酸容器中聚合反应失控爆炸\"}', '1969年神奈川县丙烯酸容器中聚合反应失控爆炸,丙烯酸雾滴分散在空气中,蒸汽爆炸,反应因温度上升而失控，丙烯酸蒸汽压上升,罐内剩下不含聚合抑制剂的丙烯酸,丙烯酸在反复熔融和凝固中，聚合抑制剂像液体一侧转移并被汲出,为了从罐中取出丙烯酸，对罐加热并用泵汲出，然后罐放置冷却,丙烯酸冬季在罐内凝固,点火源');
INSERT INTO `event_tft_table` VALUES (38, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"设备检修\"}],\"gradeId\":\"\",\"label\":\"反应器阀门关闭，停止原料输送\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"关闭反应器出口闭锁阀\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"搅拌机停止转动\"}],\"gradeId\":\"IVD2\",\"label\":\"D2$温度没有充分下降，反应器内某个局部进行着放热反应\"}],\"gradeId\":\"IVD1\",\"label\":\"D1$温度和压力不断上升，反应失控\"}],\"gradeId\":\"\",\"label\":\"反应器安全阀启动，安全阀入口的法兰盘喷出火焰\"}],\"gradeId\":\"\",\"label\":\"反应器上部及侧面人孔的法兰盘处窜起巨大火灾\"}],\"gradeId\":\"\",\"label\":\"2人因烧伤死亡\"}],\"gradeId\":\"\",\"label\":\"1973年横滨市乙烯基降冰片烯制造中的火灾事故\"}', '1973年横滨市乙烯基降冰片烯制造中的火灾事故,2人因烧伤死亡,反应器上部及侧面人孔的法兰盘处窜起巨大火灾,反应器安全阀启动，安全阀入口的法兰盘喷出火焰,反应器阀门关闭，停止原料输送,设备检修,关闭反应器出口闭锁阀,搅拌机停止转动');
INSERT INTO `event_tft_table` VALUES (39, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"用苛性钠溶液清洗管道作业中，误开阀门\"}],\"gradeId\":\"\",\"label\":\"苛性钠溶液流入容器底部\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"容器底部有无水马来酸\"}],\"gradeId\":\"IVD3\",\"label\":\"D3$发生中和反应热，温度上升\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"钠离子催化作用\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"无水马来酸具有生成二氧化碳放热分解的特性\"}],\"gradeId\":\"IVD1\",\"label\":\"D1$分解反应加速，反应热蓄积引起反应失控，生成大量二氧化碳\"}],\"gradeId\":\"\",\"label\":\"容器内压急剧上升\"}],\"gradeId\":\"\",\"label\":\"容器爆炸\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"事故现场存在相关人员\"}],\"gradeId\":\"\",\"label\":\"2人死亡\"}],\"gradeId\":\"\",\"label\":\"1960年美国密西西比州无水马来酸分解爆炸事故\"}', '1960年美国密西西比州无水马来酸分解爆炸事故,2人死亡,容器爆炸,容器内压急剧上升,苛性钠溶液流入容器底部,用苛性钠溶液清洗管道作业中，误开阀门,容器底部有无水马来酸,钠离子催化作用,无水马来酸具有生成二氧化碳放热分解的特性,事故现场存在相关人员');
INSERT INTO `event_tft_table` VALUES (40, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"矿渣流速突然变快\"}],\"gradeId\":\"\",\"label\":\"大量高温熔融矿渣流到地面\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"地面存在积水\"}],\"gradeId\":\"VE1\",\"label\":\"E1$高温熔融矿渣与水直接接触\"}],\"gradeId\":\"\",\"label\":\"1970年富山县特种金属冶炼厂爆炸事故P239\"}', '1970年富山县特种金属冶炼厂爆炸事故P239,大量高温熔融矿渣流到地面,矿渣流速突然变快,地面存在积水');
