INSERT INTO `event_tft_table` VALUES (61, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"公司安排清理油罐内残油\"}],\"gradeId\":\"\",\"label\":\"长时间罐内清油作业，罐内油气不断挥发\"}],\"gradeId\":\"IIB5\",\"label\":\"B5$油气从人孔排出\"}],\"gradeId\":\"IIB1\",\"label\":\"B1$罐外作业现场油气达到爆炸极限\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"施工队没有进行清罐作业的专业知识\"}],\"gradeId\":\"\",\"label\":\"使用电气不防爆\"}],\"gradeId\":\"\",\"label\":\"关闭开关产生电火花\"}],\"gradeId\":\"\",\"label\":\"作业现场油气爆炸起火\"}],\"gradeId\":\"\",\"label\":\"大火从人孔钻进油罐\"}],\"gradeId\":\"\",\"label\":\"油罐内可燃蒸汽爆燃\"}],\"gradeId\":\"\",\"label\":\"储罐爆炸起火\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"相关人员在现场进行作业\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"横扫地面流淌的油火\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"通过人孔向罐内注入泡沫\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"调集沙袋，封堵人孔\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"对油罐进行冷却处理\"}],\"gradeId\":\"\",\"label\":\"消防措施\"}],\"gradeId\":\"\",\"label\":\"1人死亡，5人烧伤，大火被扑灭\"}],\"gradeId\":\"\",\"label\":\"2002年某化工厂原油储罐爆炸事故P297\"}', '2002年某化工厂原油储罐爆炸事故P297,1人死亡，5人烧伤，大火被扑灭,储罐爆炸起火,油罐内可燃蒸汽爆燃,大火从人孔钻进油罐,作业现场油气爆炸起火,长时间罐内清油作业，罐内油气不断挥发,公司安排清理油罐内残油,关闭开关产生电火花,使用电气不防爆,施工队没有进行清罐作业的专业知识,相关人员在现场进行作业,消防措施,横扫地面流淌的油火,通过人孔向罐内注入泡沫,调集沙袋，封堵人孔,对油罐进行冷却处理');
INSERT INTO `event_tft_table` VALUES (62, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"or\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"罐顶上的孔洞、呼吸孔等用布、木塞等进行简单堵塞\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"采样口用盖简单盖住\"}],\"gradeId\":\"\",\"label\":\"污油罐密封不严\"}],\"gradeId\":\"IIB5\",\"label\":\"B5$污油罐内可燃气体泄漏\"}],\"gradeId\":\"\",\"label\":\"可燃气体逸出飘散至角钢支撑与待焊伴热线结合处周围\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"空气\"}],\"gradeId\":\"IIB1\",\"label\":\"B1$形成爆炸性混合气体空间\"},{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"施工队给含硫污水管线安装伴热线\"}],\"gradeId\":\"\",\"label\":\"电焊施工接通电路\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"电焊施工利用阀门、管线、金属框架作二次回路\"}],\"gradeId\":\"\",\"label\":\"符合电焊施工型杂散电流产生间隙放电的条件\"}],\"gradeId\":\"\",\"label\":\"接通电路的瞬间，杂散电流产生火花\"}],\"gradeId\":\"\",\"label\":\"爆炸性混合气体遇放电火花产生爆炸\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"污油罐顶部有孔洞、呼吸孔或采样口\"}],\"gradeId\":\"\",\"label\":\"回火至污油罐内\"}],\"gradeId\":\"\",\"label\":\"污油罐内可燃气体爆炸起火\"}],\"gradeId\":\"\",\"label\":\"爆炸产生气浪\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"管工正在污油罐竖梯上进行管线校正作业 \"}],\"gradeId\":\"\",\"label\":\"管工受伤\"}],\"gradeId\":\"\",\"label\":\"2001年某企业污油罐爆炸事故P301\"}', '2001年某企业污油罐爆炸事故P301,管工受伤,爆炸产生气浪,污油罐内可燃气体爆炸起火,回火至污油罐内,爆炸性混合气体遇放电火花产生爆炸,可燃气体逸出飘散至角钢支撑与待焊伴热线结合处周围,污油罐密封不严,罐顶上的孔洞、呼吸孔等用布、木塞等进行简单堵塞,采样口用盖简单盖住,空气,接通电路的瞬间，杂散电流产生火花,符合电焊施工型杂散电流产生间隙放电的条件,电焊施工接通电路,施工队给含硫污水管线安装伴热线,电焊施工利用阀门、管线、金属框架作二次回路,污油罐顶部有孔洞、呼吸孔或采样口,管工正在污油罐竖梯上进行管线校正作业 ');
