INSERT INTO `event_tft_table` VALUES (72, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"向轻柴油罐卸油\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"石脑油罐与轻柴油罐共用一条卸油总管\"},{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"轻柴油罐总阀关闭，不能向轻柴油罐卸油\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"石脑油罐总阀和石脑油A罐分阀处于开启状态\"}],\"gradeId\":\"\",\"label\":\"卸轻柴油操作时阀门处于错开错关状态\"}],\"gradeId\":\"\",\"label\":\"输油管向满载的石脑油罐输入大量轻柴油\"}],\"gradeId\":\"IIB8\",\"label\":\"B8$石脑油A罐内物料超出安全液位\"}],\"gradeId\":\"IIB7\",\"label\":\"B7$石脑油A罐冒顶跑料\"}],\"gradeId\":\"IIB7\",\"label\":\"B7$石脑油A罐内石脑油、轻柴油泄漏\"}],\"gradeId\":\"IIB4\",\"label\":\"B4$泄漏出的轻质物料挥发形成可燃气体\"}],\"gradeId\":\"IIB3\",\"label\":\"B3$容器外积聚形成可燃气体蒸汽云\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"空气\"}],\"gradeId\":\"IIB1\",\"label\":\"B1$可燃气体与空气形成爆炸性蒸汽云团\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"IIB2\",\"label\":\"B2$爆炸性蒸汽云团不断扩散\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"泄漏后延迟点火\"}],\"gradeId\":\"\",\"label\":\"蒸气云爆炸，未气化的石脑油起火燃烧\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"乙烯B罐在爆炸燃烧范围内\"}],\"gradeId\":\"\",\"label\":\"乙烯B罐因被烧烤出现塑性变形开裂\"}],\"gradeId\":\"\",\"label\":\"乙烯B罐内液相乙烯沸腾爆炸\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"乙烯A罐与B罐相邻\"}],\"gradeId\":\"\",\"label\":\"乙烯A罐被爆炸冲击波向西推倒\"}],\"gradeId\":\"IIB9\",\"label\":\"B9$乙烯A罐底部管线断开\"}],\"gradeId\":\"IIB6\",\"label\":\"B6$大量液态乙烯从管口喷出\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"爆炸产生的明火\"}],\"gradeId\":\"\",\"label\":\"形成剧烈燃烧的大火\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"火场有相关工作人员\"}],\"gradeId\":\"\",\"label\":\"事故造成9人死亡\"}],\"gradeId\":\"\",\"label\":\"东方化工厂“6·27”特大火灾事故\"}', '东方化工厂“6·27”特大火灾事故,事故造成9人死亡,形成剧烈燃烧的大火,乙烯A罐被爆炸冲击波向西推倒,乙烯B罐内液相乙烯沸腾爆炸,乙烯B罐因被烧烤出现塑性变形开裂,蒸气云爆炸，未气化的石脑油起火燃烧,输油管向满载的石脑油罐输入大量轻柴油,向轻柴油罐卸油,石脑油罐与轻柴油罐共用一条卸油总管,卸轻柴油操作时阀门处于错开错关状态,轻柴油罐总阀关闭，不能向轻柴油罐卸油,石脑油罐总阀和石脑油A罐分阀处于开启状态,空气,泄漏后延迟点火,乙烯B罐在爆炸燃烧范围内,乙烯A罐与B罐相邻,爆炸产生的明火,火场有相关工作人员');
INSERT INTO `event_tft_table` VALUES (73, '{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"undefined\",\"children\":[{\"andor\":\"and\",\"children\":[{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"氨罐使用前未作认真的安全检查\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"充装和运输液氨时未执行有关规定\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"液氨贮罐制造质量低劣，焊接不合格，焊后未进行整体退火处理\"}],\"gradeId\":\"IIB11\",\"label\":\"B11$液氨贮罐在运输过程中罐体开裂\"}],\"gradeId\":\"IIB6\",\"label\":\"B6$罐内液氨泄漏\"}],\"gradeId\":\"IIB4\",\"label\":\"B4$泄漏出的液氨挥发\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"空气\"}],\"gradeId\":\"IIB1\",\"label\":\"B1$形成爆炸性混合气体\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"点火源\"}],\"gradeId\":\"\",\"label\":\"液氨贮罐爆炸\"},{\"andor\":\"\",\"children\":[],\"gradeId\":\"\",\"label\":\"爆炸现场油赶集的农民和其他人员\"}],\"gradeId\":\"\",\"label\":\"4人死亡，87人被氨雾灼伤、中毒\"}],\"gradeId\":\"\",\"label\":\"1987年安徽省毫州市化肥厂液氨贮罐爆炸事故P179\"}', '1987年安徽省毫州市化肥厂液氨贮罐爆炸事故P179,4人死亡，87人被氨雾灼伤、中毒,液氨贮罐爆炸,氨罐使用前未作认真的安全检查,充装和运输液氨时未执行有关规定,液氨贮罐制造质量低劣，焊接不合格，焊后未进行整体退火处理,空气,点火源,爆炸现场油赶集的农民和其他人员');
